(()=>{const t=async()=>{const t=await fetch("https://api.coingecko.com/api/v3/simple/price?ids=bitcoin&vs_currencies=usd",{method:"GET",headers:{"Content-Type":"application/json",Accept:"application/json"}});return(await t.json()).bitcoin.usd};document.addEventListener("DOMContentLoaded",(async function(e){document.getElementById("btcusd")&&(document.getElementById("btcusd").innerText=await t()),document.getElementById("satsusd")&&(document.getElementById("satsusd").innerText=await(async()=>{const e=await t();return parseInt(e)/1e9})())}))})();