document.addEventListener("DOMContentLoaded",(async function(t){document.getElementById("btcusd").innerText=await(async()=>{const t=await fetch("https://api.coingecko.com/api/v3/simple/price?ids=bitcoin&vs_currencies=usd",{method:"GET",headers:{"Content-Type":"application/json",Accept:"application/json"}});return(await t.json()).bitcoin.usd})()}));