(()=>{const t=async()=>{const t=await fetch("https://api.coingecko.com/api/v3/simple/price?ids=bitcoin&vs_currencies=usd",{method:"GET",headers:{"Content-Type":"application/json",Accept:"application/json"}});return(await t.json()).bitcoin.usd},e=async()=>{const e=await t();return parseInt(e)/1e8};document.addEventListener("DOMContentLoaded",(async function(n){document.getElementById("btcusd")&&(document.getElementById("btcusd").innerText=await t()),document.getElementById("satsusd")&&(document.getElementById("satsusd").innerText=await e()),document.getElementById("btcform")&&document.getElementById("btcbutton").addEventListener("click",(()=>{!async function(e){const n={btc:document.getElementById("btc").value}.btc;if(isNaN(n))document.getElementById("btcformoutput").innerText="Invalid Input";else{const e=await t(),c=parseFloat(n)*e;document.getElementById("btcformoutput").innerText=c+" $"}}()})),document.getElementById("satsform")&&document.getElementById("satsbutton").addEventListener("click",(()=>{!async function(t){const n={satoshi:document.getElementById("satoshi").value}.satoshi;if(isNaN(n))document.getElementById("satsformoutput").innerText="Invalid Input";else{const t=await e(),c=parseFloat(n)*t;document.getElementById("satsformoutput").innerText=c+" $"}}()}))}))})();